'use client'
import { jsPDF } from 'jspdf'

import { useState, useEffect, useCallback } from 'react'
import Link from 'next/link'

interface Learner {
  id: string
  name: string
}

const csoBandLabels: Record<string,string> = { critico:'Crítico', atencao:'Atenção', baixo:'Baixo', moderado:'Moderado', bom:'Bom', otimo:'Ótimo', excelente:'Excelente' }
const protocolStatusLabels: Record<string,string> = { draft:'Rascunho', active:'Ativo', mastered:'Dominado', generalization:'Generalização', maintained:'Mantido', archived:'Arquivado', suspended:'Suspenso', discontinued:'Descontinuado' }

export default function RelatoriosPage() {
  const [learners, setLearners] = useState<Learner[]>([])
  const [selectedId, setSelectedId] = useState<string>('')
  const [report, setReport] = useState<any>(null)
  const [loading, setLoading] = useState(false)
  const [loadingLearners, setLoadingLearners] = useState(true)
  const [saving, setSaving] = useState(false)
  const [saved, setSaved] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [csoHistory, setCsoHistory] = useState<any[]>([])
  const [periodStart, setPeriodStart] = useState(() => {
    const d = new Date(); d.setDate(d.getDate() - 30)
    return d.toISOString().split('T')[0]
  })
  const [periodEnd, setPeriodEnd] = useState(() => new Date().toISOString().split('T')[0])

  const fetchLearners = useCallback(() => {
    fetch('/api/aba/learners')
      .then(r => r.json())
      .then(d => { setLearners(d.learners || []); setLoadingLearners(false) })
      .catch(() => setLoadingLearners(false))
  }, [])

  useEffect(() => { fetchLearners() }, [fetchLearners])

  const generateReport = async () => {
    if (!selectedId) return
    setLoading(true)
    setError(null)
    setReport(null)
    setSaved(false)
    try {
      const res = await fetch('/api/aba/reports?learner_id=' + selectedId + '&period_start=' + periodStart + '&period_end=' + periodEnd)
      if (!res.ok) {
        const err = await res.json()
        setError(err.error || 'Erro ao gerar relatório')
        setLoading(false)
        return
      }
      const data = await res.json()
      setReport(data)
      try {
        const csoRes = await fetch('/api/aba/learners/' + selectedId + '/cso-history')
        if (csoRes.ok) { const csoData = await csoRes.json(); setCsoHistory(csoData.history || []) }
        else { setCsoHistory([]) }
      } catch { setCsoHistory([]) }
      setLoading(false)
    } catch {
      setError('Falha de conexão.')
      setLoading(false)
    }
  }

  const saveSnapshot = async () => {
    if (!selectedId || !report) return
    setSaving(true)
    try {
      const res = await fetch('/api/aba/reports', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          learner_id: selectedId,
          report_type: 'convenio',
          report_data: report.convenio,
        }),
      })
      if (res.ok) setSaved(true)
      setSaving(false)
    } catch {
      setSaving(false)
    }
  }

  const conv = report?.convenio
  const altaRaw = report?.alta_parcial
  const alta = altaRaw ? {
    eligible: altaRaw.criteria_met === true,
    criteria_met: altaRaw.details ? [altaRaw.details.weeks_met, altaRaw.details.maintained_met, altaRaw.details.regressions_met].filter(Boolean).length : 0,
    criteria_total: 3,
    recommendation: altaRaw.recommendation || null
  } : null

  const downloadPDF = () => {
    if (!conv) return
    const doc = new jsPDF()
    const w = doc.internal.pageSize.getWidth()
    const logoB64 = 'data:image/png;base64,'
    doc.addImage(logoB64, 'PNG', w/2 - 25, 8, 50, 12)
    let y = 28
    doc.setFontSize(16)
    doc.text("Relatório para Convênio", w/2, y, { align: "center" })
    y += 8
    doc.setFontSize(9)
    doc.setTextColor(100)
    doc.text("Conforme SBNI, RN 469/2021, RN 541/2022, RN 539/2022", w/2, y, { align: "center" })
    y += 10
    doc.setDrawColor(200)
    doc.line(15, y, w-15, y)
    y += 8
    doc.setFontSize(10)
    doc.setTextColor(0)
    doc.text('Aprendiz: ' + (conv.learner?.name || '--'), 15, y)
    y += 6
    doc.text('Período: ' + (conv.period?.start || '--') + ' a ' + (conv.period?.end || '--'), 15, y)
    y += 6
    doc.text('Diagnóstico: ' + (conv.learner?.diagnosis || '--') + ' (' + (conv.learner?.cid_code || '--') + ')', 15, y)
    y += 6
    doc.text('Nível de suporte: ' + (conv.learner?.support_level || '--'), 15, y)
    y += 10
    doc.setFontSize(12)
    doc.text("Resumo Clínico", 15, y)
    y += 8
    doc.setFontSize(10)
    doc.text('Sessões realizadas: ' + (conv.summary?.total_sessions || 0), 15, y)
    y += 6
    doc.text('Horas de terapia: ' + (conv.summary?.total_hours || 0) + 'h', 15, y)
    y += 6
    doc.text('CSO-ABA atual: ' + (conv.summary?.cso_aba_current ?? '--') + ' (' + (csoBandLabels[conv.summary?.cso_band_current] || conv.summary?.cso_band_current || '--') + ')', 15, y)
    y += 10
    // ── Justificativa de Carga Horária ──────────────────────────────────
    const nivel = String(conv.learner?.support_level || '')
    const nivelNum = nivel.includes('1') ? 1 : nivel.includes('2') ? 2 : nivel.includes('3') ? 3 : 0
    const horasFaixas: Record<number, { min: number; max: number; texto: string }> = {
      1: { min: 10, max: 15, texto: 'Nível 1 (baixo suporte): perfil de aquisição com maior autonomia. SBNI recomenda 10-15h/semana.' },
      2: { min: 20, max: 25, texto: 'Nível 2 (suporte moderado): generalização e independência de prompts em andamento. SBNI recomenda 20-25h/semana.' },
      3: { min: 30, max: 40, texto: 'Nível 3 (alto suporte): necessidade intensiva de intervenção. SBNI recomenda 30-40h/semana.' },
    }
    if (nivelNum > 0) {
      doc.setFontSize(12)
      doc.setTextColor(0)
      doc.text("Justificativa de Carga Horária", 15, y)
      y += 7
      doc.setFontSize(9)
      doc.setTextColor(60)
      const faixa = horasFaixas[nivelNum]
      const horasRealizadas = Number(conv.summary?.total_hours || 0)
      const semanas = conv.period?.weeks || Math.round(horasRealizadas / ((faixa.min + faixa.max) / 2)) || 1
      const hSemana = semanas > 0 ? (horasRealizadas / semanas).toFixed(1) : '--'
      const justLines = doc.splitTextToSize(faixa.texto, w - 30)
      doc.text(justLines, 15, y)
      y += justLines.length * 5 + 2
      doc.text(`Horas realizadas no período: ${horasRealizadas}h em aprox. ${semanas} semanas = ${hSemana}h/semana.`, 15, y)
      y += 5
      const adequado = horasRealizadas / semanas >= faixa.min && horasRealizadas / semanas <= faixa.max
      doc.setTextColor(adequado ? 34 : 180, adequado ? 120 : 60, adequado ? 34 : 0)
      doc.text(adequado ? 'OK - Carga horaria adequada conforme faixa recomendada.' : `ATENCAO - Carga fora da faixa recomendada (${faixa.min}-${faixa.max}h/semana). Justificativa clínica necessária.`, 15, y)
      doc.setTextColor(0)
      y += 10
    }
    // Dimensoes
    if (conv.dimensions_current) {
      doc.setFontSize(12)
      doc.text("Dimensões CSO-ABA", 15, y)
      y += 8
      doc.setFontSize(10)
      doc.text('SAS (Aquisição): ' + (conv.dimensions_current.sas ?? '--'), 15, y); y += 6
      doc.text('PIS (Independência): ' + (conv.dimensions_current.pis ?? '--'), 15, y); y += 6
      doc.text('BSS (Estabilidade): ' + (conv.dimensions_current.bss ?? '--'), 15, y); y += 6
      doc.text('TCM (Consistência): ' + (conv.dimensions_current.tcm ?? '--'), 15, y); y += 10
    }
    if (conv.protocols && conv.protocols.length > 0) {
      doc.setFontSize(12)
      doc.text('Protocolos (' + conv.protocols.length + ')', 15, y)
      y += 8
      doc.setFontSize(9)
      conv.protocols.forEach((p: any) => {
        if (y > 270) { doc.addPage(); y = 20 }
        doc.text('• ' + p.title + ' - ' + p.domain + ' - ' + (protocolStatusLabels[p.status] || p.status), 15, y)
        y += 5
      })
      y += 5
    }
    if (alta) {
      doc.setFontSize(12)
      doc.text("Alta Parcial", 15, y)
      y += 8
      doc.setFontSize(10)
      doc.text((alta.eligible ? 'Elegível' : 'Não elegível') + ' - ' + alta.criteria_met + '/' + alta.criteria_total + ' criterios', 15, y)
      y += 10
    }
    // Grafico Evolucao CSO-ABA com dimensões
    if (csoHistory.length >= 2) {
      if (y > 150) { doc.addPage(); y = 20 }
      doc.setFontSize(12)
      doc.setTextColor(0)
      doc.text("Evolução CSO-ABA", 15, y)
      y += 8
      const chartX = 20
      const chartW = w - 40
      const chartH = 70
      const chartY = y
      const chartBottom = chartY + chartH
      // Eixos
      doc.setDrawColor(200)
      doc.setLineWidth(0.3)
      doc.line(chartX, chartY, chartX, chartBottom)
      doc.line(chartX, chartBottom, chartX + chartW, chartBottom)
      // Labels Y
      doc.setFontSize(7)
      doc.setTextColor(150)
      for (let v = 0; v <= 100; v += 25) {
        const ly = chartBottom - (v / 100) * chartH
        doc.line(chartX - 2, ly, chartX + chartW, ly)
        doc.text(String(v), chartX - 4, ly + 1, { align: 'right' })
      }
      // Faixas coloridas (fundo sutil)
      doc.setFillColor(254, 242, 242); doc.rect(chartX, chartBottom - (49/100)*chartH, chartW, (49/100)*chartH, 'F')
      doc.setFillColor(255, 251, 235); doc.rect(chartX, chartBottom - (69/100)*chartH, chartW, (20/100)*chartH, 'F')
      doc.setFillColor(236, 253, 245); doc.rect(chartX, chartBottom - (84/100)*chartH, chartW, (15/100)*chartH, 'F')
      doc.setFillColor(219, 245, 219); doc.rect(chartX, chartBottom - chartH, chartW, (16/100)*chartH, 'F')

      const stepX = chartW / (csoHistory.length - 1)

      // Dimensões — linhas finas atrás do CSO principal
      const dimLines: { key: string; label: string; color: [number, number, number] }[] = [
        { key: 'sas', label: 'SAS', color: [59, 130, 246] },
        { key: 'pis', label: 'PIS', color: [168, 85, 247] },
        { key: 'bss', label: 'BSS', color: [34, 197, 94] },
        { key: 'tcm', label: 'TCM', color: [234, 179, 8] },
      ]
      for (const dim of dimLines) {
        const hasData = csoHistory.some((h: any) => h[dim.key] != null)
        if (!hasData) continue
        doc.setDrawColor(...dim.color)
        doc.setLineWidth(0.5)
        let prevPx = 0, prevPy = 0, prevValid = false
        for (let i = 0; i < csoHistory.length; i++) {
          const val = Number(csoHistory[i][dim.key])
          if (isNaN(val) || val === 0) { prevValid = false; continue }
          const px = chartX + i * stepX
          const py = chartBottom - (val / 100) * chartH
          if (prevValid) { doc.line(prevPx, prevPy, px, py) }
          doc.setFillColor(...dim.color)
          doc.circle(px, py, 0.8, 'F')
          prevPx = px; prevPy = py; prevValid = true
        }
      }

      // CSO principal — linha grossa por cima
      doc.setDrawColor(196, 106, 47)
      doc.setLineWidth(1.2)
      for (let i = 0; i < csoHistory.length; i++) {
        const px = chartX + i * stepX
        const val = Number(csoHistory[i].cso_aba)
        const py = chartBottom - (val / 100) * chartH
        if (i > 0) {
          const prevX = chartX + (i-1) * stepX
          const prevVal = Number(csoHistory[i-1].cso_aba)
          const prevY = chartBottom - (prevVal / 100) * chartH
          doc.line(prevX, prevY, px, py)
        }
        doc.setFillColor(196, 106, 47)
        doc.circle(px, py, 1.5, 'F')
        // Valor acima do ponto
        doc.setFontSize(7)
        doc.setTextColor(196, 106, 47)
        doc.text(String(val), px, py - 3, { align: 'center' })
        // Data abaixo do eixo
        doc.setTextColor(150)
        doc.setFontSize(6)
        const d = new Date(csoHistory[i].session_date)
        doc.text(d.toLocaleDateString('pt-BR', {day:'2-digit', month:'short'}), px, chartBottom + 5, { align: 'center' })
      }
      // Legenda faixas
      doc.setFontSize(6)
      doc.setTextColor(100)
      const legY = chartBottom + 10
      doc.text('Crítico 0-49 | Atenção 50-69 | Bom 70-84 | Excelente 85-100', w/2, legY, { align: 'center' })

      // Legenda dimensões
      const dimLegY = legY + 6
      let dimLegX = 30
      // CSO principal
      doc.setFillColor(196, 106, 47); doc.rect(dimLegX, dimLegY - 2, 8, 2, 'F')
      doc.setFontSize(6); doc.setTextColor(80)
      doc.text('CSO-ABA', dimLegX + 10, dimLegY)
      dimLegX += 30
      for (const dim of dimLines) {
        doc.setFillColor(...dim.color); doc.rect(dimLegX, dimLegY - 2, 8, 2, 'F')
        doc.setTextColor(80)
        doc.text(dim.label, dimLegX + 10, dimLegY)
        dimLegX += 22
      }
      y = dimLegY + 8
    }

    doc.setDrawColor(200)
    doc.line(15, y, w-15, y)
    y += 6
    doc.setFontSize(7)
    doc.setTextColor(130)
    doc.text("Motor CSO-ABA v2.6.1. Indicadores descritivos, não diagnósticos. Julgamento clínico é sempre humano.", 15, y)
    y += 4
    doc.text('Gerado em: ' + new Date().toLocaleString('pt-BR') + ' - AXIS Clínico', 15, y)
    const name = (conv.learner?.name || 'relatorio').replace(/\s+/g, '_')
    doc.save('AXIS_Relatorio_' + name + '.pdf')
  }

  return (
    <>
      <div className="flex justify-center pt-5 md:pt-6 mb-6 md:mb-8 px-4">
        <nav className="inline-flex items-center gap-1 bg-white border border-slate-200 rounded-full px-3 md:px-5 py-1.5 shadow-sm">
          <Link href="/aba" className="px-3 py-1 text-sm font-medium text-slate-400 hover:text-slate-600 transition-colors">Painel</Link>
          <span className="text-slate-300 text-xs">·</span>
          <Link href="/aba/aprendizes" className="px-3 py-1 text-sm font-medium text-slate-400 hover:text-slate-600 transition-colors">Aprendizes</Link>
          <span className="text-slate-300 text-xs">·</span>
          <Link href="/aba/sessoes" className="px-3 py-1 text-sm font-medium text-slate-400 hover:text-slate-600 transition-colors">Sessões</Link>
          <span className="text-slate-300 text-xs hidden sm:inline">·</span>
          <Link href="/aba/relatorios" className="hidden sm:inline px-3 py-1 text-sm font-medium text-aba-500">Relatórios</Link>
        </nav>
      </div>

      <div className="px-4 md:px-8 lg:px-12 xl:px-16">
        <header className="mb-6">
          <h1 className="text-lg font-normal text-slate-400 tracking-tight mb-0">Relatórios</h1>
          <p className="text-xs text-slate-300 font-light">Gerar relatório para convênio em 1 clique</p>
        </header>

        <div className="max-w-3xl">
          <div className="space-y-3 mb-8">
            <div className="flex gap-3">
              <select value={selectedId} onChange={e => { setSelectedId(e.target.value); setReport(null); setSaved(false) }} className="flex-1 px-3 py-2 border border-slate-200 rounded-lg text-sm focus:outline-none focus:border-aba-500 bg-white">
                <option value="">{loadingLearners ? 'Carregando...' : 'Selecione o aprendiz'}</option>
                {learners.map(l => (<option key={l.id} value={l.id}>{l.name}</option>))}
              </select>
            </div>
            <div className="flex flex-col sm:flex-row gap-3 sm:items-end">
              <div className="flex gap-3 flex-1">
                <div className="flex-1">
                  <label className="block text-xs text-slate-500 mb-1">Início do período</label>
                  <input type="date" value={periodStart} onChange={e => setPeriodStart(e.target.value)} className="w-full px-3 py-2 border border-slate-200 rounded-lg text-sm focus:outline-none focus:border-aba-500" />
                </div>
                <div className="flex-1">
                  <label className="block text-xs text-slate-500 mb-1">Fim do período</label>
                  <input type="date" value={periodEnd} onChange={e => setPeriodEnd(e.target.value)} className="w-full px-3 py-2 border border-slate-200 rounded-lg text-sm focus:outline-none focus:border-aba-500" />
                </div>
              </div>
              <button onClick={generateReport} disabled={!selectedId || loading} className="w-full sm:w-auto px-5 py-2 bg-aba-500 text-white text-sm font-medium rounded-lg hover:bg-aba-600 transition-colors disabled:opacity-50 whitespace-nowrap">
                {loading ? 'Gerando...' : 'Gerar'}
              </button>
            </div>
          </div>

          {error && (
            <div className="mb-6 p-3 bg-red-50 rounded-lg">
              <p className="text-xs text-red-500">{error}</p>
            </div>
          )}

          {conv && (
            <div className="space-y-6">
              <div className="border border-slate-200 rounded-xl overflow-hidden">
                <div className="bg-slate-50 px-5 py-3 border-b border-slate-200">
                  <h2 className="text-sm font-medium text-slate-700">Relatório para Convênio</h2>
                  <p className="text-xs text-slate-400 mt-0.5">
                    {conv.learner?.name} · {conv.period?.start} a {conv.period?.end} · Motor v{conv.engine_version}
                  </p>
                </div>

                <div className="p-5 space-y-4">
                  <div className="grid grid-cols-2 sm:grid-cols-4 gap-4">
                    <div className="text-center p-3 bg-aba-500/5 rounded-lg">
                      <p className="text-xl font-light text-aba-500">{conv.summary?.total_sessions || 0}</p>
                      <p className="text-[11px] text-slate-400 mt-1">Sessões realizadas</p>
                    </div>
                    <div className="text-center p-3 bg-aba-500/5 rounded-lg">
                      <p className="text-xl font-light text-aba-500">{conv.summary?.total_hours || 0}h</p>
                      <p className="text-[11px] text-slate-400 mt-1">Horas de terapia</p>
                    </div>
                    <div className="text-center p-3 bg-aba-500/5 rounded-lg">
                      <p className="text-xl font-light text-aba-500">{conv.summary?.cso_aba_current ?? '—'}</p>
                      <p className="text-[11px] text-slate-400 mt-1">CSO-ABA atual</p>
                    </div>
                    <div className="text-center p-3 bg-aba-500/5 rounded-lg">
                      <p className="text-xl font-light text-aba-500">{csoBandLabels[conv.summary?.cso_band_current] || conv.summary?.cso_band_current || '—'}</p>
                      <p className="text-[11px] text-slate-400 mt-1">Banda CSO</p>
                    </div>
                  </div>

                  {conv.dimensions_current && (conv.dimensions_current.sas || conv.dimensions_current.pis || conv.dimensions_current.bss || conv.dimensions_current.tcm) && (
                    <div className="pt-3 border-t border-slate-100">
                      <h3 className="text-xs font-medium text-slate-600 mb-2">Dimensões atuais</h3>
                      <div className="grid grid-cols-2 sm:grid-cols-4 gap-3">
                        {['sas', 'pis', 'bss', 'tcm'].map(dim => (
                          <div key={dim} className="text-center">
                            <p className="text-sm font-medium text-slate-700">{conv.dimensions_current[dim] ?? '—'}</p>
                            <p className="text-[10px] text-slate-400 uppercase">{dim}</p>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {conv.protocols && conv.protocols.length > 0 && (
                    <div className="pt-3 border-t border-slate-100">
                      <h3 className="text-xs font-medium text-slate-600 mb-2">Protocolos ({conv.protocols.length})</h3>
                      <div className="space-y-2">
                        {conv.protocols.map((p: any, i: number) => (
                          <div key={i} className="flex items-center justify-between gap-2 p-2 bg-slate-50 rounded-lg">
                            <div className="min-w-0">
                              <p className="text-xs font-medium text-slate-700 truncate">{p.title}</p>
                              <p className="text-[10px] text-slate-400 truncate">{p.domain} · {p.ebp_practice_pt || p.ebp_practice}</p>
                            </div>
                            <span className={'text-[10px] px-2 py-0.5 rounded-full shrink-0 ' + (p.status === 'active' ? 'bg-green-50 text-green-600' : p.status === 'mastered' ? 'bg-blue-50 text-blue-600' : 'bg-slate-100 text-slate-500')}>
                              {protocolStatusLabels[p.status] || p.status}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {conv.legal_footer && (
                    <div className="pt-3 border-t border-slate-100">
                      <p className="text-[10px] text-slate-400 italic">{conv.legal_footer}</p>
                    </div>
                  )}
                </div>
              </div>

              {alta && (
                <div className={'border rounded-xl p-5 ' + (alta.eligible ? 'border-green-200 bg-green-50/50' : 'border-slate-200')}>
                  <div className="flex items-center gap-3">
                    <div className={'w-8 h-8 rounded-full flex items-center justify-center ' + (alta.eligible ? 'bg-green-100' : 'bg-slate-100')}>
                      <svg className={'w-4 h-4 ' + (alta.eligible ? 'text-green-600' : 'text-slate-400')} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={alta.eligible ? "M5 13l4 4L19 7" : "M12 8v4m0 4h.01"} />
                      </svg>
                    </div>
                    <div>
                      <h3 className="text-sm font-medium text-slate-700">
                        {alta.eligible ? 'Elegível para alta parcial' : 'Não elegível para alta parcial'}
                      </h3>
                      <p className="text-xs text-slate-400">
                        {alta.criteria_met}/{alta.criteria_total} critérios atendidos
                      </p>
                    </div>
                  </div>
                </div>
              )}

              <div className="flex flex-col sm:flex-row justify-end gap-3 pt-2">
                <button onClick={downloadPDF} className="w-full sm:w-auto px-5 py-2 text-sm font-medium rounded-lg border border-aba-500 text-aba-500 hover:bg-aba-500/5 transition-colors">Download PDF</button>
                <button onClick={saveSnapshot} disabled={saving || saved} className={'w-full sm:w-auto px-5 py-2 text-sm font-medium rounded-lg transition-colors ' + (saved ? 'bg-green-50 text-green-600 border border-green-200' : 'bg-aba-500 text-white hover:bg-aba-600 disabled:opacity-50')}>
                  {saved ? '✓ Versão registrada' : saving ? 'Registrando...' : 'Registrar Versão Oficial'}
                </button>
              </div>

              <p className="text-[11px] text-slate-300 italic">
                Versão oficial registrada com hash SHA256. Imutável após registro.
              </p>
            </div>
          )}

          {!conv && !loading && !error && selectedId && (
            <div className="text-center py-12">
              <p className="text-xs text-slate-400">Selecione o período e clique em "Gerar"</p>
            </div>
          )}

          {!selectedId && !loadingLearners && learners.length === 0 && (
            <div className="text-center py-16">
              <div className="w-16 h-16 rounded-full bg-aba-500/10 flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-aba-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
              </div>
              <p className="text-sm text-slate-500 mb-1">Nenhum aprendiz cadastrado</p>
              <p className="text-xs text-slate-400">Cadastre aprendizes antes de gerar relatórios</p>
            </div>
          )}
        </div>
      </div>
    </>
  )
}
